import {useState} from 'react';
function App(){
    const [clickedbutton, setClickedButton] = useState(false);
    const [name, setName] = useState("");
    const[formsubmitted, setFormSubmitted] = useState(false);
    const [disabled, setDisabled] = useState('');
    const handleClick = ()=>{
        setClickedButton(!clickedbutton);
    }
    const handleChange = (event)=>{
        setName(event.target.value);
    }
    const handleSubmit= (event)=>{
        event.preventDefault();
        setFormSubmitted(true);
    }
    let content = clickedbutton? <div>This is a cool thing that is appearing!</div>: <div></div>
    let content2 = formsubmitted ? <div>Hello, {name}, how are you doing today?<button>Good</button><button>Bad</button></div>: <div></div>
    return(
        <div>
            <label>This is dynamic disappearing app!</label>
            <hr></hr>
            <button onClick={handleClick}>Click!</button>
            {content}
            <hr></hr>
            <label>Please enter your name:</label>
            <form onSubmit={handleSubmit}>
                <input disabled={disabled} value={name} onChange={handleChange}></input>
            </form>
            {content2}
        </div>
    )
}
export default App;
