import axios from 'axios';
function Task({task, id}){
    const handleDelete = async()=>{
        await axios.post('http://localhost:5004/deleteitem', {data: id});
    }
    return(
        <div>
            {task}
            <button onClick={handleDelete}>Remove Task</button>
        </div>
    )
}
export default Task;
