import {useState} from 'react';
function App(){
    const [clickedbutton, setClickedButton] = useState(false);
    const [name, setName] = useState("");
    const handleClick = ()=>{
        setClickedButton(!clickedbutton);
    }
    const handleChange = (event)=>{
        setName(event.target.value);
    }
    const handleSubmit= (event)=>{
        event.preventDefault();

    }
    let content = clickedbutton? <div>This is a cool thing that is appearing!</div>: <div></div>
    let content2 = name.length>0 ? <div>Hello, {name}, how are you doing today?</div>: <div></div>
    return(
        <div>
            <label>This is dynamic disappearing app!</label>
            <hr></hr>
            <button onClick={handleClick}>Click!</button>
            {content}
            <hr></hr>
            <label>Please enter your name:</label>
            <input onSubmit={handleSubmit} value={name} onChange={handleChange}></input>
            {content2}
        </div>
    )
}
export default App;
